  `update_time` datetime(0) NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT 'Update time',
  `update_user` int(11) NOT NULL DEFAULT 0 COMMENT 'Updater ID',
  PRIMARY KEY (`carousel_id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 8 CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = Dynamic;


