  `selling_price` int(11) NOT NULL DEFAULT 1 COMMENT 'Selling Price',
  `stock_num` int(11) NOT NULL DEFAULT 0 COMMENT 'Stock Quantity',
  `tag` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '' COMMENT 'Goods Tag',
