-- Table structure for tb_index_config
-- ----------------------------
DROP TABLE IF EXISTS `tb_index_config`;
CREATE TABLE `tb_index_config` (
`config_id` bigint(20) NOT NULL AUTO_INCREMENT COMMENT 'Home page configuration item primary key id',
