-- ----------------------------
-- Table structure for tb_order
-- ----------------------------
DROP TABLE IF EXISTS `tb_order`;
CREATE TABLE `tb_order` (
`order_id` bigint(20) NOT NULL AUTO_INCREMENT COMMENT 'Order table primary key id',
`order_no` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '' COMMENT 'Order number',
`user_id` bigint(20) NOT NULL DEFAULT 0 COMMENT 'User primary key id',
`total_price` int(11) NOT NULL DEFAULT 1 COMMENT 'Total order price',
`pay_status` tinyint(4) NOT NULL DEFAULT 0 COMMENT 'Payment status: 0. Not paid, 1. Successful payment, -1: Failed payment',
`pay_type` tinyint(4) NOT NULL DEFAULT 0 COMMENT '0. None 1. Alipay 2. WeChat Pay',
`pay_time` datetime(0) NULL DEFAULT NULL COMMENT 'Payment time',
`order_status` tinyint(4) NOT NULL DEFAULT 0 COMMENT 'Order status: 0. Waiting for payment 1. Paid 2. Distribution completed 3: Successful shipment 4. Transaction successful -1. Manually closed -2. Timeout closed -3. Merchant closed',
`extra_info` varchar(100) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '' COMMENT 'Order body',
`user_name` varchar(30) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '' COMMENT 'Consignees name',
`user_phone` varchar(11) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '' COMMENT 'Consignees phone number',
`user_address` varchar(100) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '' COMMENT 'Consignees delivery address',
`is_deleted` tinyint(4) NOT NULL DEFAULT 0 COMMENT 'Delete identification field (0-not deleted 1-deleted)',
`create_time` datetime(0) NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT 'Creation time',
`update_time` datetime(0) NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT 'Latest modification time',
PRIMARY KEY (`order_id`) USING BTREE ) ENGINE = InnoDB AUTO_INCREMENT = 21 CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = Dynamic;

