`user_id` bigint(20) NOT NULL AUTO_INCREMENT COMMENT 'User primary key id',
`nick_name` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '' COMMENT 'User nickname',
